sparql:
PREFIX ling: <http://purl.org/linguistics#>
SELECT ?name ?iso ?family ?lat ?long
WHERE {
    <https://glottolog.org/resource/languoid/id/abip1241> rdfs:label ?name ;
                 ling:languageFamily ?family ;
                 geo:location ?loc .
    OPTIONAL { <https://glottolog.org/resource/languoid/id/abip1241> ling:iso639P3code ?iso }
    ?loc geo:lat ?lat ;
         geo:long ?long .
}

